public static void write(final StringBuilder sb,
                             final HasImports model) {
        final Imports imports = model.getImports();
        if (imports == null) {
            return;
        }
        sb.append(imports.toString());
        if (imports.getImports().size() > 0) {
            sb.append("\n");
        }
    }
