@Test
    public void testProcessResultsHasNothingToReport() throws IOException {
        Logger mockLogger = mock(Logger.class);
        ResultsProcessor.processResults(new ArrayList<>(),
                new ArrayList<>(),
                mockLogger);
        verify(mockLogger, times(0)).info(any(), anyList());
    }

