        when(gitHubUtil.startSearch()).thenReturn(ghContentSearchBuilder);

        dockerfileGitHubUtil = new DockerfileGitHubUtil(gitHubUtil);
        assertEquals(dockerfileGitHubUtil.findFilesWithImage("test", "test"), list);
    }

