public void removeConsumersForSessionId(final String sessionId) {
        synchronized (consumers) {
            consumers
                .entrySet()
                .stream()
                .filter((entry) -> entry.getKey().getSessionId().equals(sessionId))
                .forEach((entry) -> entry.getValue().requestStop());
        }
    }
