public static String toString(Object[] a) {
		if (a == null) {
			return "null";
		}

		if (a.length == 0) {
			return "[]";
		}

		StringBuilder b = new StringBuilder("[");
		boolean first = true;
		for (Object o : a) {
			if (!first) {
				b.append(", ");
			} else {
				first = false;
			}
			b.append(o);
		}
		b.append(']');
		return b.toString();
	}
