public static String getExtension(final String mimeType)
    {
        if (MIME_TYPES_EXTENSIONS.containsKey(mimeType))
        {
            return MIME_TYPES_EXTENSIONS.get(mimeType);
        }
        return mimeType.substring(mimeType.indexOf("/") + 1);
    }
