public Graph apply(final Graph g) {

        Graph h = new Graph(g.order());

        // copy atom/topology information
        for (int u = 0; u < g.order(); u++) {
            h.addAtom(g.atom(u));
            h.addTopology(g.topologyOf(u));
        }

        // apply edges
        for (int u = 0; u < g.order(); u++) {
            for (final Edge e : g.edges(u)) {
                if (e.other(u) > u)
                    h.addEdge(toExplicitEdge(g, e));
            }
        }

        return h;
    }
