@Test
    public void test_noChangeNoKeyguardFeaturesDisabled() throws Exception {
        int result = KeyguardFeatures.setFingerprintDisabled(
                DevicePolicyManager.KEYGUARD_DISABLE_FEATURES_NONE, false);
        assertEquals(DevicePolicyManager.KEYGUARD_DISABLE_FEATURES_NONE, result);
    }

focal_method.body:
static int setFingerprintDisabled(
            int currentDisabledFeatures,
            boolean fingerprintDisabled) {
                int keyguardDisabledFeatures;
        if(fingerprintDisabled){
            keyguardDisabledFeatures =
                    currentDisabledFeatures | DevicePolicyManager.KEYGUARD_DISABLE_FINGERPRINT;
        } else {
            keyguardDisabledFeatures =
                    currentDisabledFeatures & (~DevicePolicyManager.KEYGUARD_DISABLE_FINGERPRINT);
        }
        return keyguardDisabledFeatures;
    }
