public static String getUnitString(final Context context, final UnitSystem system) {
        switch (system) {
            case IMPERIAL_UK:
            case IMPERIAL_US:
                return context.getString(R.string.msdkui_unit_miles_per_hour);
            case METRIC:
            default: // metric
                return context.getString(R.string.msdkui_unit_km_per_h);
        }
    }
