public static byte[] decode(final String base64Encoded) {
    try {
      if (null == base64Encoded || base64Encoded.isEmpty()) {
        return new byte[0];
      }
      return BaseEncoding.base64().decode(base64Encoded);
    } catch (Exception e) {
      throw new IllegalArgumentException(e);
    }
  }
