public static String makeFilename(final String name, final String ext) {
    if (null == name || null == ext) {
      return null;
    }
    if (isEmpty(ext)) {
      return name;
    }
    final StringBuilder buffer = new StringBuilder();
    if (!isEmpty(name)) {
      buffer.append(nvl(name));
    }
    buffer.append('.');
    buffer.append(ext);

    return buffer.toString();
  }
