@Test
  public void testAppendPrefixOnEmptyByteArray() {
    final byte prefix = randomUUID().toString().getBytes()[0];
    final byte[] source = null;
    final byte[] appended = BytesValueUtils.append(source, prefix);
    assertEquals(prefix, appended[0]);
  }

focal_method.body:
public static BytesValue append(final BytesValue target, final byte prefix) {
    if (null == target) {
      return BytesValue.of(append(BytesValue.EMPTY.getValue(), prefix));
    }
    return BytesValue.of(append(target.getValue(), prefix));
  }
