@Test
    public void getReplaceableTermsOfQueryIgnoresSinglePrefixOperator() {
        // given
        var simpleQuery = "*";

        // when
        var actualPairs = HibernateSimpleQueryUtils.getReplaceableTermsOfQuery(simpleQuery);

        // then
        assertThat(actualPairs).isEmpty();
    }

