public Envelope combine(List<GetLocationResult> results) {
		Envelope result = null;
		for (GetLocationResult add : results) {
			Envelope envelope = add.getEnvelope();
			if (null == result) {
				result = envelope;
			} else {
				result = result.intersection(envelope);
			}
		}
		return result;
	}
