@Test
    public void isAlgorithmSupported() throws Exception {
        Assert.assertEquals(true, CompressionUtil.isAlgorithmSupported("gzip"));
        Assert.assertEquals(true, CompressionUtil.isAlgorithmSupported("zip"));
