@Test
    public void testConvert__array() {

        // dane wejsciowe
        Object[][] arrays = {
            {1L, 2L, 3L},
            {}
        };

        // oczekiwane rezultaty
        Object[][] expectations = {
            new IntegerHolder[]{new IntegerHolder(1L), new IntegerHolder(2L), new IntegerHolder(3L)},
            new IntegerHolder[]{}
        };

        // wykonanie testow
        for (int i = 0; i < arrays.length; i++) {
            Object[] array = arrays[i];
            IntegerHolder[] expected = (IntegerHolder[]) expectations[i];

            ValueHolder[] result = TypeDecoder.convert(type, array);
            checkArrays(expected, result);
        }
    }

focal_method.body:
public static ValueHolder convert(Type<?> type, Object obj) {
        try {
            return type.convert(obj);
        } catch (RuntimeException exception) {
            throw new TypeConversionException(exception, obj, type);
        }
    }
