@Test
    public void testGet__cache() {

        // wykonanie testu
        SimpleDateFormat sdf1 = SimpleDateFormatPool.get("dd/MM/yyyy");
        SimpleDateFormat sdf2 = SimpleDateFormatPool.get("dd/MM/yyyy");

        // weryfikacja identycznosci
        assertSame(sdf1, sdf2);
    }

