public static SimpleDateFormat get(String pattern) {

        Map<String, SimpleDateFormat> map = pool.get();

        SimpleDateFormat sdf = map.get(pattern);
        if (sdf == null) {
            sdf = new SimpleDateFormat(pattern);
            sdf.setLenient(false);
            map.put(pattern, sdf);
        }
        return sdf;
    }
