@Test
    public void testPrint__null() {

        // kolekcja wejsciowa
        List<Integer> list = null;
        Integer[] array = null;

        // wykonanie testu
        String result1 = Printer.print(list, "Integer list");
        String result2 = Printer.print(array, "Integer array");

        // weryfikacja
        assertNull(result1);
        assertNull(result2);
    }

