public static <T>Enumeration<T> enumeration(final T[] a) {
    return new Enumeration<T>() {
      private int i = -1;
      private final int len = a.length - 1;

      @Override
      public boolean hasMoreElements() {
        return i < len;
      }

      @Override
      public T nextElement() {
        return a[++i];
      }
    };
  }
