@Test
    public void convert() {
        ZonedDateTime zdt =
                ZoneDateTimeUtils.truncatedTo(Instant.ofEpochSecond(1546041600).atZone(ZoneOffset.UTC), ChronoUnit.DAYS);

        assertEquals(1546041600, zdt.toInstant().getEpochSecond());
    }

