@Test
    public void shouldSplit() {
        assertThat(Strings.split("c,s,v", ',')).contains("c", "s", "v");
        assertThat(Strings.split("c.s.v", '.')).contains("c", "s", "v");
        assertThat(Strings.split("c,s,v,", ',')).contains("c", "s", "v");
        assertThat(Strings.split("  , csv , x, y ", ',')).contains("csv", "x", "y");
        assertThat(Strings.split(",,,", ',')).isEmpty();
    }

