@Test
    public void testNullCodeSourceWithDelegateJavaagent() throws Exception {
        System.setProperty("glowroot.test.dir", ".");
        try {
            assertThat(AgentPremain.getGlowrootJarFile(null)).isNull();
        } finally {
            System.clearProperty("glowroot.test.dir");
        }
    }

