@Test
    public void testMaterialize() {
        Series<String> s = new OffsetLeadSeries<>(Series.forData("a", "b", "c", "d"), 1, "X").materialize();
        assertTrue(s instanceof ArraySeries);
        new SeriesAsserts(s).expectData("X", "a", "b", "c");
    }

