@Override
    public void parseQRCode(String QRCode) {
        if (isViewAttached()) {
            if (TextUtils.isEmpty(QRCode)) {
                return;
            }
            List<String> words = Arrays.asList(QRCode.split(" "));
            if (words != null && words.size() == 12) {
                getView().showMnemonicWords(words);
            }
        }
    }
