@Test
	public void testInvalidSign() {

		boolean exception = false;
		try {
			OadrXMLSignatureHandler.sign("moauiccool", privateKey, certificate, NONCE, 0L);
		} catch (Oadr20bXMLSignatureException e) {
			exception = true;
		}
		assertTrue(exception);

	}

