@Test
    public void testQueueSize() throws Exception {
        stripedExecSvc.execute(1, new TestRunnable());
        stripedExecSvc.execute(1, new TestRunnable(true));
        stripedExecSvc.execute(1, new TestRunnable(true));

        sleepASec();

        assertEquals(1, stripedExecSvc.queueSize());
    }

