public static String toStringDump(PageLockDump pageLockDump) {
        StringBuilder sb = new StringBuilder();

        ToStringDumpProcessor proc = new ToStringDumpProcessor(sb, String::valueOf);

        proc.processDump(pageLockDump);

        return sb.toString();
    }
