public static Response channelNotBound(String channelName, CloudEvent cloudEvent) {
        return Response.
                serverError().
                entity(new ResponseError(String.format(ERROR_CHANNEL_NOT_BOUND, channelName, Printer.beautify(cloudEvent))))
                .build();
    }
