private static final String CREATE_TRIGGER_TMPLT = "CREATE TRIGGER %s "
            + "AFTER INSERT OR UPDATE OR DELETE ON %s "
            + "FOR EACH ROW EXECUTE PROCEDURE %s('%s')";

