@Test
    public void testSetBytes() throws Exception {
        byte[] input = { 0, 1 };
        byte[] data = { 6, 0 };

        BitBufferHelper.setBytes(data, input, 7, 9);
        Assert.assertTrue(data[0] == 6);
        Assert.assertTrue(data[1] == 1);
    }

