@Override
    public void describeTo(final Description description) {
        for (int i = 0; i < matchers.size(); i++) {
            description.appendDescriptionOf(matchers.get(i));
            if (i + 1 < matchers.size()) {
                description.appendText(" or ");
            }
        }
    }
