static int determineSectionSize(int indexOfRowDimension, Variable variable) {
        int size = 1;
        for (int i = indexOfRowDimension; i < variable.getDimensions().size(); i++) {
            size *= variable.getDimension(i).getLength();
        }
        return size;
    }
