@Test
    public void testIsValid() {
        final Consul consul = new Consul(System.getProperty(EP_PROPERTY));
        assertTrue(consul.isValid());

        final Consul unavailable = new Consul("http://localhost/");
        assertFalse(unavailable.isValid());
    }

