public boolean isValid() {
        try {
            final Response response = httpClient
                    .target(endpoint)
                    .path("/v1/kv/")
                    .request()
                    .get();
            return response.getStatus() == Response.Status.BAD_REQUEST.getStatusCode();
        } catch (final Exception ce) {
            return false;
        }
    }
