public static byte[] decode(String encoded) throws U2fBadInputException {
        try {
            return BASE64_DECODER.decode(encoded);
        } catch (IllegalArgumentException e) {
            throw new U2fBadInputException("Bad base64 encoding", e);
        }
    }
