@Test
    public void match_should_succeed_in_strict_mode() throws Exception {
        //given
        final boolean strict = true;
        final String reference = "Jimmy";
        final String value = "jimmy";

        //when
        final boolean result = StringsHelper.match(reference, value, strict);

        //then
        assertTrue(result);
    }

focal_method.body:
public static boolean match(final String reference, final String value, final boolean strict) {
        return strict ? StringUtils.equalsIgnoreCase(reference, value)
                : StringUtils.containsIgnoreCase(reference, value);
    }
