@Test
    public void shoudPassthrough() throws Exception {
        // Given
        final RuntimeException runtimeException = new RuntimeException();
        final Exception exception = new Exception();

        // When
        try {
            Defaults.passthrough().apply(runtimeException);
        } catch (Exception e) {
            // Then
            assertEquals(runtimeException, e);
        }

        // When
        try {
            Defaults.passthrough().apply(exception);
        } catch (TDPException e) {
            // Then
            assertEquals(CommonErrorCodes.UNEXPECTED_EXCEPTION, e.getCode());
        }
    }

focal_method.body:
public static Function<Exception, RuntimeException> passthrough() {
        return e -> {
            if (e instanceof RuntimeException) {
                return (RuntimeException) e;
            } else {
                return new TDPException(CommonErrorCodes.UNEXPECTED_SERVICE_EXCEPTION, e,
                        build().put("message", e.getMessage()));
            }
        };
    }
