@Test
    public void postDeleteNull() throws Exception {
        mTripTableActionAlterations.postDelete(null)
                .test()
                .assertNoValues()
                .assertNotComplete()
                .assertError(Exception.class);

        verifyZeroInteractions(mReceiptsTable, mDistanceTable);
    }

