public static boolean update(Survey survey, List<Leg> legs, InputMode inputMode) {
        boolean anyStationsAdded = false;
        for (Leg leg : legs) {
            anyStationsAdded = anyStationsAdded || update(survey, leg, inputMode);
        }
        return anyStationsAdded;
    }
