@Test
	public void testBadArgs() throws Exception {
		try {
			FeedEvictor.main(null);
			Assert.fail("Expected an exception to be thrown");
		} catch (Exception ignore) { }

		try {
			FeedEvictor.main(new String[] {"1","2"});
			Assert.fail("Expected an exception to be thrown");
		} catch (Exception ignore) { }
	}

