    public void testSingleDependency() {
        String dep = "g1:a2:v3";
        Set<String> deps = new HashSet<>();
        deps.add(dep);
        String generatedKey = new ExecutionEngine() {}.generatedDependenciesKey(deps);
        assertNotNull(generatedKey);
        assertEquals(dep, generatedKey);
    }

