	public void testGetParts() {
		String s = "abcdefg";
		List<String> parts = Splitter.getParts(s, 3);

		assertEquals(parts.get(0), "abc");
		assertEquals(parts.get(1), "def");
		assertEquals(parts.get(2), "g");

