@Override
  public void prependTo(@NotNull final RawSqlBuilder builder) {
    builder.prepend(')');

    for (int i = mColumns.length - 1; i >= 0; i--) {
      mColumns[i].prependTo(builder);
      if (i > 0) {
        builder.prepend(',');
      }
    }

    builder.prepend('(');
  }
