public static boolean extractBooleanHeader(HttpResponse response, ResponseHeader responseHeader, boolean defaultValue) {
        String header = extractHeader(response, responseHeader);
        if (header == null) {
            return defaultValue;
        }
        return header.equals("1");
    }
