@Test
    public void simple() throws Exception {
        Fixture fixture = new Fixture.Builder("simple")
                .segments("foo", "bar")
                .expected("foo", "bar")
                .build();
        SegmentProcessor.process(fixture.segments, consumer);
        assertFixture(fixture, consumer.result);
    }

