@Test
    public void testParseMissingVersion() {
        String uri = "http://www.opengis.net/def/crs/EPSG/4326";
        try {
            HTTP_URI_Parser.buildParser(uri);
            fail();
        } catch (NoSuchAuthorityCodeException e) {
            assertEquals(uri, e.getAuthorityCode());
        }
    }

