@Test
  public void understandsOverflowAuto() {
    assertEquals( SWT.V_SCROLL | SWT.H_SCROLL, Style.getOverflowProperty( "overflow:auto;color:red;" ) );
    assertEquals( SWT.NONE, Style.getOverflowProperty( "overflow:other;" ) );
  }

