@Test
  public void shouldUpdateForSecondaryBroadcast() {
    updatable.addToObservable(broadcastObservable(getApplication(), PRIMARY_ACTION, TEST_ACTION));

    sendBroadcast(new Intent(TEST_ACTION));

    assertThat(updatable, wasUpdated());
  }

