@Test
    public void next_waitPeriod2() {
        // :: Setup
        long now = 10_000;
        long last = now - 15;
        int period = 1_000;
        int grace = 5;

        // :: Act
        long next = Anticipate.next(now, last, period, grace);

        // :: Verify
        assertThat(next).isEqualTo(last + period - grace);
    }

